    private void btnResultActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnResultActionPerformed
        // TODO add your handling code here:
        
        try{

            Float Divisor = Float.parseFloat(Num.getText())/Float.parseFloat(Dem.getText());

            Float Mod = Float.parseFloat(Num.getText())%Float.parseFloat(Dem.getText());

            consc.setText(Divisor.toString());
            rest.setText(Mod.toString());
            userText.setText(null);
        
        }catch(Exception e){
            userText.setText("Ops! digite os valores corretamente.");
        }
    }//GEN-LAST:event_btnResultActionPerformed

